const slider=()=>{new Splide(".splide.pc",{pagination:!1,interval:3e3}).mount(),new Splide(".splide.proyectos-slider",{pagination:!1,interval:3e3,perPage:3,trimSpace:!1,breakpoints:{1024:{perPage:2},600:{perPage:1}}}).mount(),document.querySelectorAll(".item-button").forEach(i=>{i.addEventListener("click",e=>{var t=i.parentElement.firstElementChild.nextElementSibling;i.classList.toggle("active"),t.classList.toggle("active")})})};export{slider};