const wow=()=>{document.querySelectorAll(".animate__animated").forEach(i=>{i.style.opacity="0",window.addEventListener("scroll",function(){var t=i.getBoundingClientRect().top,e=i.getBoundingClientRect().bottom,a=window.innerHeight;(0<=t&&t<=a||0<=e&&e<=a)&&(i.dataset.delay?setTimeout(()=>{i.style.opacity="1",i.classList.add(""+i.dataset.animate)},i.dataset.delay):(i.style.opacity="1",i.classList.add(""+i.dataset.animate)))})})};export{wow};